import React from 'react'
import {Link, NavLink} from 'react-router-dom'
import TagList from 'components/tagList'
import AddToFavorites from 'components/addToFavorites'

const Feed = ({articles}) => {
    return (
        <div>
            {articles.map((article, index) => {
                const createdDate = new Date(Date.parse(article.createdAt));
                return (
                <div className='article-preview' key={index}>
                    <div className='article-meta'>
                        <div className='info'>
                        <Link to={`/Medium/profiles/${article.author.username}`}
                              className='author'>
                            <img className='user-pic'
                                 src={article.author.image}
                                 alt=''
                            />
                            &nbsp; {article.author.username}
                        </Link>
                        <span className='date'>{createdDate.toUTCString().replace('GMT', '')}</span>
                        </div>
                        <div className='pull-xs-right'>
                            <AddToFavorites
                                isFavorited={article.favorited}
                                favoritesCount={article.favoritesCount}
                                articleSlug={article.slug}
                            />
                        </div>
                    </div>
                    <Link to={`/Medium/articles/${article.slug}`}
                          className='preview-link'>
                        <h1>{article.title}</h1>
                        <p>{article.description}</p>
                        <span>Read more...</span>
                <TagList tags={article.tagList}/>
                    </Link>
                </div>

            )})}
        </div>
    )
};
export default Feed;