import React, {useContext, Fragment} from 'react'
import {NavLink, Link} from 'react-router-dom'

import {CurrentUserContext} from 'contexts/currentUser'

const Topbar = () => {
    const [currentUserState] = useContext(CurrentUserContext);
  return (
    <nav className='navbar navbar-light'>
      <div className='container'>
        <Link to='/Medium' className='navbar-brand'>
          Medium
        </Link>
        <ul className='nav navbar-nav pull-xs-right'>
          <li className='nav-item'>
            <NavLink to='/Medium' className='nav-link' exact>
              Home
            </NavLink>
          </li>
            {currentUserState.isLoggedIn === false && (
                <Fragment>
                    <li className='nav-item'>
                        <NavLink to='/Medium/login' className='nav-link'>
                            Sign in
                        </NavLink>
                    </li>
                    <li className='nav-item'>
                        <NavLink to='/Medium/register' className='nav-link'>
                            Sign up
                        </NavLink>
                    </li>
                </Fragment>
            )}
            {currentUserState.isLoggedIn && (
                <Fragment>
                    <li className='nav-item'>
                        <NavLink to='/Medium/create-post' className='nav-link'>
                            <i className='ion-compose'/>
                            &nbsp; New Post
                        </NavLink>
                    </li>
                  <li className='nav-item'>
                    <NavLink to='/Medium/settings' className='nav-link'>
                      <i className='ion-gear-a' />
                      &nbsp; Settings
                    </NavLink>
                  </li>
                    <li className='nav-item'>
                        <NavLink to={`/Medium/profiles/${currentUserState.currentUser.username}`} className='nav-link'>
                        <img className='user-pic'
                             src={currentUserState.currentUser.image}
                             alt=''
                        />
                        &nbsp; {currentUserState.currentUser.username}
                        </NavLink>
                    </li>
                </Fragment>
            )}

        </ul>
      </div>
    </nav>
  )
};

export default Topbar
