import React from 'react'
import {Switch, Route} from 'react-router-dom'

import GlobalFeed from 'pages/globalFeed'
import YourFeed from 'pages/yourFeed'
import TagFeed from 'pages/tagFeed'
import Article from 'pages/article'
import Authentication from 'pages/authentication'
import CreateArticle from 'pages/createArticle'
import EditArticle from 'pages/editArticle'
import Settings from 'pages/settings'
import UserProfile from 'pages/userProfile'

const Routes = () => {
  return (
    <Switch>
      <Route path='/Medium' component={GlobalFeed} exact />
      <Route path='/Medium/settings' component={Settings} />
      <Route path='/Medium/profiles/:slug' component={UserProfile} />
      <Route path='/Medium/profiles/:slug/favorites' component={UserProfile} />
      <Route path='/Medium/feed' component={YourFeed} />
      <Route path='/Medium/tags/:slug' component={TagFeed} />
      <Route path='/Medium/articles/:slug' component={Article} exact />
      <Route path='/Medium/articles/:slug/edit' component={EditArticle} />
      <Route path='/Medium/create-post' component={CreateArticle} exact />
      <Route path='/Medium/login' component={Authentication} />
      <Route path='/Medium/register' component={Authentication} />
      <Route path='*' component={GlobalFeed} />
    </Switch>
  )
};
export default Routes
